mapper.select:
SELECT
award_id,award_name,award_desc,award_img,point,priority,create_time,last_edit_time,enable_status
FROM tb_award

    shop_id = #{awardCondition.shopId}
    and award_name like '%${awardCondition.awardName}%'
    and point = #{awardCondition.point}
    and enable_status = #{awardCondition.enableStatus}

order by priority DESC
LIMIT #{rowIndex},#{pageSize}

SELECT count(1)
FROM tb_award

    and award_name like '%${awardCondition.awardName}%'
    and point = #{awardCondition.point}
    and enable_status = #{awardCondition.enableStatus}

SELECT
award_id,award_name,award_desc,award_img,point,priority,create_time,last_edit_time,enable_status
FROM tb_award
WHERE award_id = #{awardId}
AND shop_id = #{shopId}

mapper.update:
UPDATE tb_award

    award_name = #{award.awardName}
    award_desc = #{award.awardDesc}
    award_img = #{award.awardImg}
    point = #{award.point}
    priority = #{award.priority}
    last_edit_time = #{award.lastEditTime}
    enable_staatus = #{award.enableStatus}

where award_id = #{award.awardId}
AND shop_id = #{shopId}

mapper.delete:
DELETE FROM tb_award
WHERE award_id = #{awardId}
AND shop_id = #{shopId}
